import turtle
import time

turtle.forward(100)
time.sleep(1) #avanza 100 pixeles adelante
turtle.right(90)
time.sleep(1) #lleva los grados q va a girar
turtle.backward(5)
time.sleep(2)
turtle.left(90)
time.sleep(1) 
turtle.circle(150)
time.sleep(3)
turtle.penup()
turtle.setx(-100)
turtle.sety(-100)
turtle.pendown()
turtle.circle(50)
time.sleep(3)

